import React, { useState } from 'react';
import { Grid, Paper, Typography, List, ListItem, ListItemText, IconButton } from '@mui/material';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import '../style/HomePage.css';
import avatar from '../images/avatar.png';

function HomePage() {
  const carouselItems = [
    { title: 'Item 1', image: avatar },
    { title: 'Item 2', image: avatar },
    { title: 'Item 3', image: avatar },
    // Add more items as needed
  ];

  const [selectedMenuItem, setSelectedMenuItem] = useState(0);

  const handleMenuItemClick = (index) => {
    setSelectedMenuItem(index);
  };

  const handlePrev = () => {
    setSelectedMenuItem((prevIndex) => (prevIndex === 0 ? carouselItems.length - 1 : prevIndex - 1));
  };

  const handleNext = () => {
    setSelectedMenuItem((prevIndex) => (prevIndex === carouselItems.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div className="HomePage">
      <header className="HomePage-header">
        <Grid container spacing={2}>
          {/* Left Side - Carousel */}
          <Grid item xs={8}>
            <div className="carousel">
              <IconButton className="carousel-arrow" onClick={handlePrev}>
                <ArrowBackIcon />
              </IconButton>
              {carouselItems.map((item, index) => (
                <div
                  key={index}
                  className={`carousel-item ${index === selectedMenuItem ? 'active' : ''}`}
                >
                  <img src={item.image} alt={item.title} className="carousel-image" />
                  <Typography variant="h5" className="carousel-title">
                    {item.title}
                  </Typography>
                </div>
              ))}
              <IconButton className="carousel-arrow" onClick={handleNext}>
                <ArrowForwardIcon />
              </IconButton>
            </div>
          </Grid>

          {/* Right Side - Vertical Menu */}
          <Grid item xs={4}>
            <List component="nav" className="vertical-menu">
              {carouselItems.map((item, index) => (
                <ListItem
                  key={index}
                  button
                  selected={index === selectedMenuItem}
                  onClick={() => handleMenuItemClick(index)}
                >
                  <ListItemText primary={item.title} />
                </ListItem>
              ))}
            </List>
          </Grid>
        </Grid>
      </header>
    </div>
  );
}

export default HomePage;
