import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { AppBar, Toolbar, IconButton, Typography, Button, Paper, Grid } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import '../style/HomeBis.css';

function HomeBis() {
    const [isHovered, setIsHovered] = useState(false);

    const handleMouseEnter = () => {
        setIsHovered(true);
    }

    const handleMouseLeave = () => {
        setIsHovered(false);
    }

    return (
        <div style={{ display: 'grid', gridTemplateRows: '64px 1fr auto', height: '100vh' }}>
            <AppBar position="static" color="inherit" elevation={0}>
                <Toolbar>
                    <Link to="/" style={{ textDecoration: 'none', color: 'inherit' }}>
                        <IconButton edge="start" color="inherit" aria-label="home">
                            <HomeIcon/>
                        </IconButton>
                    </Link>
                    <Typography variant="h6" style={{ flexGrow: 1 }}>
                        {/* Placeholder for center alignment */}
                    </Typography>
                    <Link to="/Contact" style={{ textDecoration: 'none', color: '#B1B1B1' }}>
                        <Button color="inherit" style={{ color: '#B1B1B1', textTransform: 'none' }}>
                            Contact
                        </Button>
                    </Link>
                </Toolbar>
            </AppBar>

            {/* Centered text */}
            <div 
                className='test' 
                style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    transform: isHovered ? 'translateY(10%)' : 'translateX(0%)' // Conditional transform
                }}
            >
                <Typography variant="h4" style={{ color: '#B1B1B1' }}>
                    Texte de pr√©sentation random
                </Typography>
            </div>

            <div style={{ padding: '0 24px' }}>
                <Grid container spacing={3} style={{ alignItems: 'flex-end' }}>
                    {["/Bio", "/Projects", "/Experience"].map((route, index) => (
                        <Grid item  xs={12} sm={6} md={4} key={index}>
                            <Link to={route} style={{ textDecoration: 'none' }}>
                                <Paper 
                                    elevation={0} 
                                    className="grid-item-paper hoverable-grid-item"
                                    style={{ background: '#F4F3F1' }}
                                    onMouseEnter={handleMouseEnter}
                                    onMouseLeave={handleMouseLeave}
                                >
                                    <Paper elevation={5} className="inner-paper" style={{background: '#EFEFEF' }} />
                                </Paper>
                            </Link>
                        </Grid>
                    ))}
                </Grid>
            </div>
        </div>
    );
} 

export default HomeBis;
