import * as React from 'react';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import ImageListItemBar from '@mui/material/ImageListItemBar';

export default function ProjectsList() {
  const [activeTag, setActiveTag] = React.useState(null);

  const handleTagClick = (tag) => {
    setActiveTag(tag);
  };

  const buttonStyle = {
    padding: '8px 16px',
    border: 'none',
    cursor: 'pointer',
    marginRight: '8px',
  };

  const activeButtonStyle = {
    ...buttonStyle,
    backgroundColor: 'blue', // Change this to the desired color
    color: 'white',
  };

  return (
    <div>
      {/* Buttons for filtering */}
      <div>
        <button
          style={activeTag === null ? activeButtonStyle : buttonStyle}
          onClick={() => handleTagClick(null)}
        >
          All
        </button>
        <button
          style={activeTag === 'front' ? activeButtonStyle : buttonStyle}
          onClick={() => handleTagClick('front')}
        >
          Front
        </button>
        <button
          style={activeTag === 'back' ? activeButtonStyle : buttonStyle}
          onClick={() => handleTagClick('back')}
        >
          Back
        </button>
        <button
          style={activeTag === 'pro' ? activeButtonStyle : buttonStyle}
          onClick={() => handleTagClick('pro')}
        >
          Pro
        </button>
      </div>

      {/* Image list */}
      <ImageList cols={3} gap={16}>
        {itemData
          .filter((item) => activeTag === null || item.tag === activeTag)
          .map((item) => (
            <div key={item.img} style={{ border: '1px solid white', borderRadius: '10px', padding: '10px' }}>
              <ImageListItem>
                <img
                  src={`${item.img}?w=248&fit=crop&auto=format`}
                  srcSet={`${item.img}?w=248&fit=crop&auto=format&dpr=2 2x`}
                  alt={item.title}
                  loading="lazy"
                />
                <ImageListItemBar
                  title={item.title}
                  subtitle={<span>by: {item.author}</span>}
                  position="below"
                />
              </ImageListItem>
            </div>
          ))}
      </ImageList>
    </div>
  );
}

const itemData = [
  {
    img: 'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
    title: 'Breakfast',
    author: '@bkristastucchio',
    tag: 'front'
  },
  {
    img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
    title: 'Burger',
    author: '@rollelflex_graphy726',
    tag: 'back'
  },
  {
    img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
    title: 'Camera',
    author: '@helloimnik',
    tag: 'pro'
  },
  {
    img: 'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
    title: 'caca',
    author: '@bkrieefazfastastucchio',
    tag: 'front'
  },
  {
    img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
    title: 'Burgazdazder',
    author: '@rollelflex_zdaagraphy726',
    tag: 'back'
  },
  {
    img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
    title: 'Cadzdadadmera',
    author: '@heldazdzloimnik',
    tag: 'pro'
  },
];
