import React, { useState } from 'react';
import {
  Grid,
  Typography,
  List,
  ListItem,
  ListItemText,
  Paper,
  Avatar,
  Box,
} from '@mui/material';
import '../style/HomePage.css';
import avatar from '../images/avatar.png';

function HomePage() {

  return (
    <div className="HomePage">
      <header className="HomePage-header">
        <Grid container spacing={2} alignItems="center" justifyContent="center">
          {/* Left Side - Avatar and Animated Text */}
          <Grid item xs={8}>
            <Box
              sx={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                height: '100%',
                backgroundColor: 'background.default', // Match the right side background
              }}
            >
              <Paper elevation={3} className="avatar-container">
                <Avatar
                  alt="Avatar"
                  src={carouselItems[selectedMenuItem].image}
                  className="avatar"
                />
                <Typography variant="h5" className="animated-text">
                  Animated Text Below Avatar
                </Typography>
              </Paper>
            </Box>
          </Grid>

          {/* Right Side - Vertical Menu */}
          <Grid item xs={4}>
            <List component="nav" className="vertical-menu">
              {carouselItems.map((item, index) => (
                <ListItem
                  key={index}
                  button
                  selected={index === selectedMenuItem}
                  onClick={() => handleMenuItemClick(index)}
                >
                  <ListItemText primary={item.title} />
                </ListItem>
              ))}
            </List>
          </Grid>
        </Grid>
      </header>
    </div>
  );
}

export default HomePage;
