import React, { useState } from 'react';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import ImageListItemBar from '@mui/material/ImageListItemBar';

const filterTags = ["Breakfast", "Burger", "Camera"];

export default function ProjectsList() {
  const [activeTags, setActiveTags] = useState(filterTags);

  const handleTagClick = (tag) => {
    if (activeTags.includes(tag)) {
      setActiveTags(activeTags.filter((t) => t !== tag));
    } else {
      setActiveTags([...activeTags, tag]);
    }
  };

  const filteredItems = itemData.filter((item) =>
    activeTags.includes(item.title)
  );

  return (
    <div>
      <div>
        {filterTags.map((tag) => (
          <button
            key={tag}
            onClick={() => handleTagClick(tag)}
            className={activeTags.includes(tag) ? 'active' : ''}
          >
            {tag}
          </button>
        ))}
      </div>
      <ImageList sx={{ width: 500, height: 450 }} cols={3} gap={16}>
        {filteredItems.map((item) => (
          <div key={item.img} style={{ border: '2px solid white', borderRadius: '10px' }}>
            <ImageListItem>
              <img
                src={`${item.img}?w=248&fit=crop&auto=format`}
                srcSet={`${item.img}?w=248&fit=crop&auto=format&dpr=2 2x`}
                alt={item.title}
                loading="lazy"
              />
              <ImageListItemBar
                title={item.title}
                subtitle={<span>by: {item.author}</span>}
                position="below"
              />
            </ImageListItem>
          </div>
        ))}
      </ImageList>
    </div>
  );
}

const itemData = [
  {
    img: 'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
    title: 'Breakfast',
    author: '@bkristastucchio',
    tags: ['Food', 'Morning'],
  },
  {
    img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
    title: 'Burger',
    author: '@rollelflex_graphy726',
    tags: ['Fodod', 'Mornding'],
  },
  {
    img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
    title: 'Camera',
    author: '@helloimnik',
    tags: ['Fdood', 'Mordning'],
  },
  {
    img: 'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
    title: 'Breakfast',
    author: '@bkristastucchio',
    tags: ['Fodod', 'Mornding'],
  },
  {
    img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
    title: 'Burger',
    author: '@rollelflex_graphy726',
    tags: ['Fodod', 'Mornding'],
  },
  {
    img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
    title: 'Camera',
    author: '@helloimnik',
    tags: ['Foodd', 'Mornding'],
  }
];
