import * as React from 'react';
import ImageList from '@mui/material/ImageList';
import Button from '@mui/material/Button';
import ImageListItem from '@mui/material/ImageListItem';
import ImageListItemBar from '@mui/material/ImageListItemBar';
import { v4 as uuidv4 } from 'uuid'; // Import uuid

export default function ProjectsList() {
  const [activeTags, setActiveTags] = React.useState([]);

  const handleTagClick = (tag) => {
    // Check if the tag is already active
    if (activeTags.includes(tag)) {
      // Remove the tag if it's active
      setActiveTags(activeTags.filter((t) => t !== tag));
    } else {
      // Add the tag if it's not active
      setActiveTags([...activeTags, tag]);
    }
  };

  const isTagActive = (tag) => activeTags.includes(tag);

  const buttonListStyle = {
    margin: '20px',
    display: 'flex',
    justifyContent: 'center'
  };

  const buttonStyle = {
    backgroundColor: '#282c34',
    color: 'white',
  };

  const activeButtonStyle = {
    ...buttonStyle,
    backgroundColor: 'white', // Change this to the desired color
    color: '#282c34',
  };

  const filteredItems = itemData.filter((item) => {
    // Check if all selected tags are present in the item's tags
    return activeTags.length === 0 || activeTags.every((tag) => item.tags.includes(tag));
  });

  return (
    <div>
      {/* Buttons for filtering */}
      <div style={buttonListStyle}>
        <Button
          style={isTagActive('Professionnel') ? activeButtonStyle : buttonStyle}
          onClick={() => handleTagClick('Professionnel')}
        >
          Professionnel
        </Button>
        <Button
          style={isTagActive('Personnel') ? activeButtonStyle : buttonStyle}
          onClick={() => handleTagClick('Personnel')}
        >
          Personnel
        </Button>
        <Button
          style={isTagActive('Front') ? activeButtonStyle : buttonStyle}
          onClick={() => handleTagClick('Front')}
        >
          Front
        </Button>
        <Button
          style={isTagActive('Back') ? activeButtonStyle : buttonStyle}
          onClick={() => handleTagClick('Back')}
        >
          Back
        </Button>
      </div>

      <ImageList sx={{gridTemplateColumns: 'repeat(auto-fill, minmax(28px, 1fr))!important'}} cols={3} gap={16} style={{margin: '50px'}}>
        {filteredItems.map((item) => (
          <ImageListItem key={uuidv4()}> {/* Generate a unique key */}
            <img
              src={`${item.img}?w=248&fit=crop&auto=format`}
              srcSet={`${item.img}?w=248&fit=crop&auto=format&dpr=2 2x`}
              alt={item.title}
              loading="lazy"
            />
            <div
              style={{
                position: 'absolute',
                bottom: '0',
                right: '0',
                background: 'rgba(0, 0, 0, 0.6)',
                color: 'white',
                padding: '4px 8px',
                fontSize: '12px',
              }}
            >
              {item.tags.join(', ')}
            </div>
            <ImageListItemBar
              title={item.title}
              subtitle={<span>{item.author}</span>}
              position="below"
            />
          </ImageListItem>
        ))}
      </ImageList>
    </div>
  );
}

const itemData = [
  {
    img: 'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
    title: 'Projet 1',
    author: '@bkristastucchio',
    tags: ['Professionnel', 'Front'],
  },
  {
    img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
    title: 'Projet 2',
    author: '@rollelflex_graphy726',
    tags: ['Personnel', 'Back'],
  },
  {
    img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
    title: 'Projet 3',
    author: '@helloimnik',
    tags: ['Front'],
  },
  {
    img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
    title: 'Projet 4',
    author: '@helzefzfzoimnik',
    tags: ['Back'],
  },
  {
    img: 'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
    title: 'Projet 5',
    author: '@bkristastucchio',
    tags: ['Front'],
  },
  {
    img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
    title: 'Projet 6',
    author: '@rollelflex_graphy726',
    tags: ['Back'],
  },
];
