import React, { useState } from 'react';
import {
  Grid,
  List,
  ListItem,
  ListItemText,
  Typography,
  MobileStepper,
  Button,
  useTheme,
} from '@mui/material';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import autoPlay from 'react-swipeable-views-utils/lib/autoPlay'; // Import autoPlay
import SwipeableViews from 'react-swipeable-views'; // Import SwipeableViews
import '../style/HomePage.css';
import avatar from '../images/avatar.png';

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

function HomePage() {
  const carouselItems = [
    { title: 'Item 1', image: avatar },
    { title: 'Item 2', image: avatar },
    { title: 'Item 3', image: avatar },
    // Add more items as needed
  ];

  const [selectedItemIndex, setSelectedItemIndex] = useState(0);

  const handleNext = () => {
    setSelectedItemIndex((prevIndex) => (prevIndex === carouselItems.length - 1 ? 0 : prevIndex + 1));
  };

  const handleBack = () => {
    setSelectedItemIndex((prevIndex) => (prevIndex === 0 ? carouselItems.length - 1 : prevIndex - 1));
  };

  const theme = useTheme();

  return (
    <div className="HomePage">
      <header className="HomePage-header">
        <Grid container spacing={2}>
          {/* Left Side - Stepper */}
          <Grid item xs={8}>
            <SwipeableViews
              index={selectedItemIndex}
              onChangeIndex={setSelectedItemIndex}
              enableMouseEvents
            >
              {carouselItems.map((item, index) => (
                <div key={index} className={`carousel-item ${index === selectedItemIndex ? 'active' : ''}`}>
                  <img src={item.image} alt={item.title} className="slider-image" />
                  <Typography variant="h5" className="slider-title">
                    {item.title}
                  </Typography>
                </div>
              ))}
            </SwipeableViews>
            <MobileStepper
              steps={carouselItems.length}
              position="static"
              variant="dots"
              activeStep={selectedItemIndex}
              nextButton={
                <Button size="small" onClick={handleNext} disabled={selectedItemIndex === carouselItems.length - 1}>
                  Next
                  {theme.direction === 'rtl' ? (
                    <ArrowBackIcon />
                  ) : (
                    <ArrowForwardIcon />
                  )}
                </Button>
              }
              backButton={
                <Button size="small" onClick={handleBack} disabled={selectedItemIndex === 0}>
                  {theme.direction === 'rtl' ? (
                    <ArrowForwardIcon />
                  ) : (
                    <ArrowBackIcon />
                  )}
                  Back
                </Button>
              }
            />
          </Grid>

          {/* Right Side - Vertical Menu */}
          <Grid item xs={4}>
            <List component="nav" className="vertical-menu">
              {carouselItems.map((item, index) => (
                <ListItem
                  key={index}
                  button
                  selected={index === selectedItemIndex}
                  onClick={() => setSelectedItemIndex(index)}
                >
                  <ListItemText primary={item.title} />
                </ListItem>
              ))}
            </List>
          </Grid>
        </Grid>
      </header>
    </div>
  );
}

export default HomePage;
