import React, { useState } from 'react';
import { Grid, Paper, Typography, List, ListItem, ListItemText, IconButton } from '@mui/material';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import '../style/HomePage.css';
import avatar from '../images/avatar.png';

function HomePage() {
  const carouselItems = [
    { title: 'Item 1', image: avatar },
    { title: 'Item 2', image: avatar },
    { title: 'Item 3', image: avatar },
    // Add more items as needed
  ];

  const [selectedItemIndex, setSelectedItemIndex] = useState(0);

  const handlePrev = () => {
    setSelectedItemIndex((prevIndex) => (prevIndex === 0 ? carouselItems.length - 1 : prevIndex - 1));
  };

  const handleNext = () => {
    setSelectedItemIndex((prevIndex) => (prevIndex === carouselItems.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div className="HomePage">
      <header className="HomePage-header">
        <Grid container spacing={2}>
          {/* Left Side - Image Slider */}
          <Grid item xs={8}>
            <Paper elevation={3} className="image-slider">
              <IconButton className="slider-arrow left" onClick={handlePrev}>
                <ArrowBackIcon />
              </IconButton>
              <img
                src={carouselItems[selectedItemIndex].image}
                alt={carouselItems[selectedItemIndex].title}
                className="slider-image"
              />
              <IconButton className="slider-arrow right" onClick={handleNext}>
                <ArrowForwardIcon />
              </IconButton>
              <Typography variant="h5" className="slider-title">
                {carouselItems[selectedItemIndex].title}
              </Typography>
            </Paper>
          </Grid>

          {/* Right Side - Vertical Menu */}
          <Grid item xs={4}>
            <List component="nav" className="vertical-menu">
              {carouselItems.map((item, index) => (
                <ListItem
                  key={index}
                  button
                  selected={index === selectedItemIndex}
                  onClick={() => setSelectedItemIndex(index)}
                >
                  <ListItemText primary={item.title} />
                </ListItem>
              ))}
            </List>
          </Grid>
        </Grid>
      </header>
    </div>
  );
}

export default HomePage;
