import React, { useState } from 'react';
import {
  Grid,
  List,
  ListItem,
  ListItemText,
  Typography,
  MobileStepper,
  Button,
  useTheme,
} from '@mui/material';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import autoPlay from 'react-swipeable-views-utils/lib/autoPlay'; // Import autoPlay
import SwipeableViews from 'react-swipeable-views'; // Import SwipeableViews
import '../style/HomePage.css';
import avatar from '../images/avatar.png';

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

function HomePage() {

  const images = [
    {
      label: 'San Francisco – Oakland Bay Bridge, United States',
      imgPath:
        'https://images.unsplash.com/photo-1537944434965-cf4679d1a598?auto=format&fit=crop&w=400&h=250&q=60',
    },
    {
      label: 'Bird',
      imgPath:
        'https://images.unsplash.com/photo-1538032746644-0212e812a9e7?auto=format&fit=crop&w=400&h=250&q=60',
    },
    {
      label: 'Bali, Indonesia',
      imgPath:
        'https://images.unsplash.com/photo-1537996194471-e657df975ab4?auto=format&fit=crop&w=400&h=250',
    },
    {
      label: 'Goč, Serbia',
      imgPath:
        'https://images.unsplash.com/photo-1512341689857-198e7e2f3ca8?auto=format&fit=crop&w=400&h=250&q=60',
    },
  ];

  const carouselItems = [
    { title: 'Item 1', image: avatar },
    { title: 'Item 2', image: avatar },
    { title: 'Item 3', image: avatar },
    // Add more items as needed
  ];

  const [selectedItemIndex, setSelectedItemIndex] = useState(0);

  const theme = useTheme();
  const [activeStep, setActiveStep] = useState(0);
  const maxSteps = images.length;

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleStepChange = (step) => {
    setActiveStep(step);
  };

  return (
    <div className="HomePage">
      <header className="HomePage-header">
        <Grid container spacing={2}>
          {/* Left Side - Stepper */}
          <Grid item xs={8}>

          </Grid>

          {/* Right Side - Vertical Menu */}
          <Grid item xs={4}>
            <List component="nav" className="vertical-menu">
              {carouselItems.map((item, index) => (
                <ListItem
                  key={index}
                  button
                  selected={index === selectedItemIndex}
                  onClick={() => setSelectedItemIndex(index)}
                >
                  <ListItemText primary={item.title} />
                </ListItem>
              ))}
            </List>
          </Grid>
        </Grid>
      </header>
    </div>
  );
}

export default HomePage;
