import React, { useState } from 'react';
import {
  Grid,
  Typography,
  List,
  ListItem,
  ListItemText,
  IconButton,
  Paper,
  Avatar,
  Box,
  CssBaseline,
  Container,
} from '@mui/material';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import '../style/HomePage.css';
import avatar from '../images/avatar.png';

function HomePage() {
  const carouselItems = [
    { title: 'Item 1', image: avatar },
    { title: 'Item 2', image: avatar },
    { title: 'Item 3', image: avatar },
    // Add more items as needed
  ];

  const [selectedMenuItem, setSelectedMenuItem] = useState(0);

  const handleMenuItemClick = (index) => {
    setSelectedMenuItem(index);
  };

  const handlePrev = () => {
    setSelectedMenuItem((prevIndex) => (prevIndex === 0 ? carouselItems.length - 1 : prevIndex - 1));
  };

  const handleNext = () => {
    setSelectedMenuItem((prevIndex) => (prevIndex === carouselItems.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className="HomePage">
        <header className="HomePage-header">
          <Grid container spacing={2} alignItems="center" justifyContent="center">
            {/* Left Side - Avatar and Animated Text */}
            <Grid item xs={8}>
              <Box
                sx={{
                  display: 'flex',
                  justifyContent: 'center',
                  alignItems: 'center',
                  height: '100%',
                  backgroundColor: 'background.default', // Match the right side background
                }}
              >
                <Paper elevation={3} className="avatar-container">
                  <Avatar
                    alt="Avatar"
                    src={carouselItems[selectedMenuItem].image}
                    className="avatar"
                  />
                  <Typography variant="h5" className="animated-text">
                    Animated Text Below Avatar
                  </Typography>
                </Paper>
              </Box>
            </Grid>

            {/* Right Side - Vertical Menu */}
            <Grid item xs={4}>
              <List component="nav" className="vertical-menu">
                {carouselItems.map((item, index) => (
                  <ListItem
                    key={index}
                    button
                    selected={index === selectedMenuItem}
                    onClick={() => handleMenuItemClick(index)}
                  >
                    <ListItemText primary={item.title} />
                  </ListItem>
                ))}
              </List>
            </Grid>
          </Grid>
        </header>
      </div>
    </Container>
  );
}

export default HomePage;
