import React, { useState } from 'react';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import ImageListItemBar from '@mui/material/ImageListItemBar';

const tagButtonStyle = {
  backgroundColor: '#ccc', /* Default background color for inactive tags */
  color: '#333', /* Default text color for inactive tags */
  padding: '5px 10px',
  margin: '5px',
  border: 'none',
  cursor: 'pointer',
};

const activeTagButtonStyle = {
  backgroundColor: '#007bff', /* Background color for active tags */
  color: '#fff', /* Text color for active tags */
};

export default function ProjectsList() {
  const [activeTags, setActiveTags] = useState([]);

  const handleTagClick = (tag) => {
    if (activeTags.includes(tag)) {
      // If the tag is already active, remove it
      setActiveTags(activeTags.filter((t) => t !== tag));
    } else {
      // If the tag is not active, add it
      setActiveTags([...activeTags, tag]);
    }
  };

  const filteredItems = itemData.filter((item) => {
    if (activeTags.length === 0) {
      return true; // Show all items if no tags are active
    }
    // Show the item if it has at least one of the active tags
    return item.tags.some((tag) => activeTags.includes(tag));
  });

  return (
    <div>
      <div>
        {Array.from(new Set(itemData.flatMap((item) => item.tags))).map((tag) => (
          <button
            key={tag}
            onClick={() => handleTagClick(tag)}
            style={{
              ...tagButtonStyle,
              ...(activeTags.includes(tag) ? activeTagButtonStyle : {}),
            }}
          >
            {tag}
          </button>
        ))}
      </div>
      <ImageList sx={{ width: 500, height: 450 }} cols={3} gap={16}>
        {filteredItems.map((item) => (
          <div key={item.img} style={{ border: '2px solid white', borderRadius: '10px' }}>
            <ImageListItem>
              <img
                src={`${item.img}?w=248&fit=crop&auto=format`}
                srcSet={`${item.img}?w=248&fit=crop&auto=format&dpr=2 2x`}
                alt={item.title}
                loading="lazy"
              />
              <ImageListItemBar
                title={item.title}
                subtitle={<span>by: {item.author}</span>}
                position="below"
              />
            </ImageListItem>
          </div>
        ))}
      </ImageList>
    </div>
  );
}

const itemData = [
  {
    img: 'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
    title: 'Breakfast',
    author: '@bkristastucchio',
    tags: ['Food', 'Morning'],
  },
  {
    img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
    title: 'Burger',
    author: '@rollelflex_graphy726',
    tags: ['Food'],
  },
  {
    img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
    title: 'Camera',
    author: '@helloimnik',
    tags: ['Technology'],
  },
  {
    img: 'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
    title: 'Morning Coffee',
    author: '@bkristastucchio',
    tags: ['Food', 'Morning'],
  },
  {
    img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
    title: 'Cheeseburger',
    author: '@rollelflex_graphy726',
    tags: ['Food'],
  },
  {
    img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
    title: 'Laptop',
    author: '@helloimnik',
    tags: ['test'],
  },
];
